  { key: "1F3FE", emoji: "üèæ", label: "Medium-Dark", color: "bg-brown-100" },
  { key: "1F3FF", emoji: "üèø", label: "Dark", color: "bg-gray-800" },
];

const unicodeToEmoji = (unified: string) => {
  return unified
    .split("-")
    .map((hex) => String.fromCodePoint(parseInt(hex, 16)))
    .join("");
};

const isEmojiSupported = (emoji: EmojiItem) => {
  if (emoji.category === "Component") return false;
  if (emoji.obsoleted_by) return false;
  if (emoji.obsoletes) return false;

  const hasImage =
    emoji.has_img_apple || emoji.has_img_google || emoji.has_img_twitter;
  if (!hasImage) return false;

  const unified = emoji.unified;
  const codepoints = unified.split("-");

  if (codepoints.length > 4) return false;

  const hasVariationSelector = unified.includes("FE0F");
  if (hasVariationSelector && emoji.non_qualified) return false;

  const isFlag = emoji.category === "Flags";
  if (isFlag && codepoints.length !== 2) return false;

  const isComplexSequence = codepoints.length > 2;
  if (isComplexSequence && !isFlag) return false;

  return true;
};

const detectDeviceSupport = () => {
  if (typeof window === "undefined" || typeof navigator === "undefined") {
    // Default values for SSR
    return {
      isApple: false,
      isAndroid: false,
      isWindows: false,
      isMobile: false,
    };
  }

  const userAgent = navigator.userAgent.toLowerCase();
  const isApple = /iphone|ipad|ipod|mac/.test(userAgent);
  const isAndroid = /android/.test(userAgent);
  const isWindows = /windows/.test(userAgent);

  return {
    isApple,
    isAndroid,
    isWindows,
    isMobile: /mobile|android|iphone|ipad|ipod/.test(userAgent),
  };
};

export interface EmojiPickerProps {
  onSelect: (emoji: string) => void;
  className?: string;
  disabled?: boolean;
}

export function EmojiPicker({
  onSelect,
  className,
  disabled,
}: EmojiPickerProps) {
  const [open, setOpen] = useState(false);
  const [search, setSearch] = useState("");
  const [selectedSkinTone, setSelectedSkinTone] = useState<string | null>(null);
  const [hoveredCategory, setHoveredCategory] = useState<string | null>(null);
  const [deviceSupport, setDeviceSupport] = useState(() =>
    detectDeviceSupport(),
  );
  const scrollRef = useRef<HTMLDivElement>(null);
  const searchRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    // Update device support on client side
    setDeviceSupport(detectDeviceSupport());
  }, []);

  const emojis = useMemo(() => {
    return (emojiData as EmojiItem[]).filter((emoji) => {
      if (!isEmojiSupported(emoji)) return false;

      const { isApple, isAndroid, isWindows } = deviceSupport;

      if (isApple && !emoji.has_img_apple) return false;
      if (isAndroid && !emoji.has_img_google) return false;
      if (isWindows && !emoji.has_img_twitter) return false;

      return true;
    });
  }, [deviceSupport]);

  const categorizedEmojis = useMemo(() => {
    const categorized: Record<string, EmojiItem[]> = {};
    CATEGORIES.forEach((cat) => {
      categorized[cat.key] = [];
    });

    emojis.forEach((emoji) => {
      if (categorized[emoji.category]) {
        categorized[emoji.category].push(emoji);
      }
    });

    return categorized;
  }, [emojis]);

  const filteredEmojis = useMemo(() => {
    if (!search) {
      return categorizedEmojis;
    }

    const searchLower = search.toLowerCase();
    const filtered: Record<string, EmojiItem[]> = {};

    Object.entries(categorizedEmojis).forEach(([category, emojiList]) => {
      const matching = emojiList.filter((emoji) => {
        const matchesName = emoji.name.toLowerCase().includes(searchLower);
        const matchesShortName = emoji.short_names.some((name) =>
          name.toLowerCase().includes(searchLower),
        );
        return matchesName || matchesShortName;
      });

      if (matching.length > 0) {
        filtered[category] = matching;
      }
    });

    return filtered;
  }, [search, categorizedEmojis]);

  const handleSelect = useCallback(
    (emoji: EmojiItem) => {
      let emojiChar = unicodeToEmoji(emoji.unified);

      if (selectedSkinTone && emoji.skin_variations?.[selectedSkinTone]) {
        const skinVariation = emoji.skin_variations[selectedSkinTone];
        emojiChar = unicodeToEmoji(skinVariation.unified);
      }

      onSelect(emojiChar);
      setOpen(false);
    },
    [onSelect, selectedSkinTone],
  );

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Escape") {
      setOpen(false);
      return;
    }
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          type="button"
          variant="ghost"
          size="icon"
          aria-label="Open emoji picker"
          disabled={disabled}
        >
          <Smile className="h-5 w-5" />
        </Button>
      </PopoverTrigger>
      <PopoverContent
        side="top"
        className={cn("w-80 p-0 z-50 overflow-hidden", className)}
        align="start"
        onKeyDown={handleKeyDown}
      >
        <div className="sticky top-0 bg-background border-b z-10">
          <div className="p-2 sm:p-3">
            <div className="relative">
              <Search className="absolute left-2 sm:left-3 top-1/2 transform -translate-y-1/2 h-3 w-3 sm:h-4 sm:w-4 text-muted-foreground" />
              <Input
                ref={searchRef}
                type="text"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                placeholder="Search emoji..."
                className="pl-7 sm:pl-10 pr-6 sm:pr-8 text-xs sm:text-sm"
                aria-label="Search emoji"
              />
              {search && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setSearch("")}
                  className="absolute right-1 top-1/2 transform -translate-y-1/2 h-5 w-5 sm:h-6 sm:w-6 p-0"
                >
                  √ó
                </Button>
              )}
            </div>
          </div>

          <div className="px-2 sm:px-3 pb-2">
            <div className="flex gap-1">
              {SKIN_TONES.map((tone) => (
                <Button
                  key={tone.key || "default"}
                  variant={selectedSkinTone === tone.key ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setSelectedSkinTone(tone.key)}
                  className={cn(
                    "h-6 w-6 sm:h-7 sm:w-7 p-0 text-xs sm:text-sm rounded-full transition-all",
                    selectedSkinTone === tone.key && "ring-2 ring-primary",
                  )}
                  title={tone.label}
                >
                  {tone.emoji}
                </Button>
              ))}
            </div>
          </div>
        </div>

        <ScrollArea ref={scrollRef} className="h-64 sm:h-80">
          <div className="p-1 sm:p-2">
            {Object.entries(filteredEmojis).map(([category, emojiList]) => {
              const categoryInfo = CATEGORIES.find(
                (cat) => cat.key === category,
              );
              if (!categoryInfo || emojiList.length === 0) return null;

              return (
                <div key={category} className="mb-4 sm:mb-6">
                  <div
                    id={`category-${category}`}
                    className={cn(
                      "flex items-center gap-1 sm:gap-2 mb-2 sm:mb-3 px-1 sm:px-2 py-1 rounded-lg transition-colors",
                      hoveredCategory === category && "bg-muted/50",
                    )}
                    onMouseEnter={() => setHoveredCategory(category)}
                    onMouseLeave={() => setHoveredCategory(null)}
                  >
                    <span className="text-sm sm:text-lg">
                      {categoryInfo.icon}
                    </span>
                    <span className="font-medium text-xs sm:text-sm text-muted-foreground">
                      {categoryInfo.label}
                    </span>
                    <span className="text-xs text-muted-foreground ml-auto">
                      {emojiList.length}
                    </span>
                  </div>

                  <div className="grid grid-cols-8 sm:grid-cols-9 gap-0.5 sm:gap-1">
                    {emojiList.map((emoji) => (
                      <Button
                        key={emoji.unified}
                        type="button"
                        variant="ghost"
                        size="icon"
                        className="flex items-center justify-center rounded-lg hover:bg-accent focus:bg-accent focus:outline-none text-sm sm:text-lg h-7 w-7 sm:h-8 sm:w-8 transition-colors"
                        onClick={() => handleSelect(emoji)}
                        title={emoji.name}
                        aria-label={emoji.name}
                      >
                        {unicodeToEmoji(emoji.unified)}
                      </Button>
                    ))}
                  </div>
                </div>
              );
            })}

            {Object.keys(filteredEmojis).length === 0 && (
              <div className="text-center text-muted-foreground py-8 sm:py-12">
                <Smile className="h-8 w-8 sm:h-12 sm:w-12 mx-auto mb-2 sm:mb-3 opacity-50" />
                <p className="text-xs sm:text-sm">No emoji found</p>
                <p className="text-xs mt-1">Try a different search term</p>
              </div>
            )}
          </div>
        </ScrollArea>
      </PopoverContent>
    </Popover>
  );
}

/**
 * SupaChat - useChat Hook
 * Core chat logic for messages, rooms, and realtime updates with sync and notifications.
 */

"use client";

import { createChatStore } from "@/hooks/supachat/use-chat-store";
import {
  createAnonymousClient,
  createChatChannel,
  createChatRoom,
  createMessage,
  createSupabaseClient,
  getMessages,
} from "@/lib/supabase/client";
import type {
  ChatMessage,
  ChatRoom,
  SupaChatConfig,
} from "@/lib/supachat/types";
import { useCallback, useEffect, useMemo, useRef, useState } from "react";

export function useChat(config: SupaChatConfig) {
  const chatStore = useMemo(
    () => createChatStore({ localStorageKey: config.localStorageKey }),
    [config.localStorageKey],
  );
  const storeMessages = chatStore((s: any) => s.messages);
  const storeCurrentRoom = chatStore((s: any) => s.currentRoom);
  const storeCurrentUser = chatStore((s: any) => s.user);
  const sessionId = chatStore((s: any) => s.sessionId);
  const setMessages = chatStore((s: any) => s.setMessages);
  const setCurrentRoom = chatStore((s: any) => s.setCurrentRoom);
  const setCurrentUser = chatStore((s: any) => s.setUser);
  const addMessage = chatStore((s: any) => s.addMessage);

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [hasNewMessages, setHasNewMessages] = useState(false);
  const [lastSyncTime, setLastSyncTime] = useState<number>(Date.now());

  const clientRef = useRef<ReturnType<typeof createSupabaseClient> | null>(
    null,
  );
  const channelRef = useRef<any>(null);
  const guestSessionIdRef = useRef<string | null>(null);
  const isPageVisibleRef = useRef<boolean>(true);

  useEffect(() => {
    const { client, getGuestSessionId } = createAnonymousClient(
      config.supabaseUrl,
      config.supabaseAnonKey,
      () => sessionId,
    );
    clientRef.current = client;
    guestSessionIdRef.current = getGuestSessionId();
  }, [config.supabaseUrl, config.supabaseAnonKey, sessionId]);

  useEffect(() => {
    const handleVisibilityChange = () => {
      const wasVisible = isPageVisibleRef.current;
      isPageVisibleRef.current = !document.hidden;

      if (wasVisible && !isPageVisibleRef.current) {
        setLastSyncTime(Date.now());
      } else if (!wasVisible && isPageVisibleRef.current) {
        if (storeCurrentRoom?.id) {
          syncNewMessages(storeCurrentRoom.id);
        }
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);
    return () => {
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, [storeCurrentRoom?.id]);

  const syncNewMessages = useCallback(
    async (roomId: string) => {
      if (!clientRef.current || !roomId) return;

      try {
        const data = await getMessages(clientRef.current, roomId);
        const transformedMessages: ChatMessage[] = data.map((msg: any) => ({
          id: msg.id,
          roomId: msg.room_id || "",
          userId: msg.user_id || undefined,
          guestSessionId: msg.guest_session_id || undefined,
          content: msg.content,
          messageType:
            (msg.message_type as "text" | "file" | "system") || "text",
          fileUrl: msg.file_url || undefined,
          fileName: msg.file_name || undefined,
          fileSize: msg.file_size || undefined,
          isFromAdmin: msg.is_from_admin || false,
          createdAt: msg.created_at || new Date().toISOString(),
        }));

        const newMessages = transformedMessages.filter(
          (msg) => new Date(msg.createdAt).getTime() > lastSyncTime,
        );

        if (newMessages.length > 0) {
          setHasNewMessages(true);
        }

        setMessages(transformedMessages);
        setLastSyncTime(Date.now());
      } catch (err) {
        console.error("Failed to sync messages:", err);
      }
    },
    [lastSyncTime, setMessages],
  );

  const loadMessages = useCallback(
    async (roomId: string) => {
      if (!clientRef.current || !roomId) return;

      setIsLoading(true);
      setError(null);

      try {
        const data = await getMessages(clientRef.current, roomId);
        const transformedMessages: ChatMessage[] = data.map((msg: any) => ({
          id: msg.id,
          roomId: msg.room_id || "",
          userId: msg.user_id || undefined,
          guestSessionId: msg.guest_session_id || undefined,
          content: msg.content,
          messageType:
            (msg.message_type as "text" | "file" | "system") || "text",
          fileUrl: msg.file_url || undefined,
          fileName: msg.file_name || undefined,
          fileSize: msg.file_size || undefined,
          isFromAdmin: msg.is_from_admin || false,
          createdAt: msg.created_at || new Date().toISOString(),
        }));
        setMessages(transformedMessages);
        setLastSyncTime(Date.now());
      } catch (err) {
        setError(
          err instanceof Error ? err.message : "Failed to load messages",
        );
      } finally {
        setIsLoading(false);
      }
    },
    [setMessages],
  );

  const sendMessage = useCallback(
    async (content: string, file?: File) => {
      if (!clientRef.current || !storeCurrentRoom?.id || !content.trim())
        return;

      setIsLoading(true);
      setError(null);

      try {
        let fileUrl: string | undefined;
        let fileName: string | undefined;
        let fileSize: number | undefined;

        if (file && config.enableUploads) {
          const uploadResult = await uploadFile(clientRef.current!, file);
          fileUrl = uploadResult.url;
          fileName = file.name;
          fileSize = file.size;
        }

        const messageData = await createMessage(
          clientRef.current!,
          storeCurrentRoom.id,
          content,
          {
            userId: storeCurrentUser?.userId,
            guestSessionId: guestSessionIdRef.current || undefined,
            isFromAdmin: storeCurrentUser?.isAdmin || false,
            messageType: file ? "file" : "text",
            fileUrl,
            fileName,
            fileSize,
          },
        );

        const newMessage: ChatMessage = {
          id: messageData.id,
          roomId: messageData.room_id || "",
          userId: messageData.user_id || undefined,
          guestSessionId: messageData.guest_session_id || undefined,
          content: messageData.content,
          messageType:
            (messageData.message_type as "text" | "file" | "system") || "text",
          fileUrl: messageData.file_url || undefined,
          fileName: messageData.file_name || undefined,
          fileSize: messageData.file_size || undefined,
          isFromAdmin: messageData.is_from_admin || false,
          createdAt: messageData.created_at || new Date().toISOString(),
        };

        addMessage(newMessage);
        setHasNewMessages(false);
      } catch (err) {
        setError(err instanceof Error ? err.message : "Failed to send message");
      } finally {
        setIsLoading(false);
      }
    },
    [storeCurrentRoom?.id, storeCurrentUser, config.enableUploads, addMessage],
  );

  const createRoom = useCallback(
    async (name?: string): Promise<ChatRoom> => {
      if (!clientRef.current) throw new Error("Client not initialized");

      try {
        const roomData = await createChatRoom(
          clientRef.current,
          true,
          guestSessionIdRef.current || undefined,
          name,
        );

        const room: ChatRoom = {
          id: roomData.id,
          name: roomData.name || undefined,
          isGuestRoom: roomData.is_guest_room || false,
          guestSessionId: roomData.guest_session_id || undefined,
          createdAt: roomData.created_at || new Date().toISOString(),
          updatedAt: roomData.updated_at || new Date().toISOString(),
          unreadCount: 0,
        };

        setCurrentRoom(room);
        await loadMessages(room.id);

        if (channelRef.current) {
          channelRef.current.unsubscribe();
        }

        channelRef.current = createChatChannel(clientRef.current, room.id);
        channelRef.current.subscribe();

        return room;
      } catch (err) {
        throw new Error(
          err instanceof Error ? err.message : "Failed to create room",
        );
      }
    },
    [loadMessages, setCurrentRoom],
  );

  const joinRoom = useCallback(
    async (roomId: string) => {
      if (!clientRef.current) throw new Error("Client not initialized");

      try {
        await loadMessages(roomId);

        if (channelRef.current) {
          channelRef.current.unsubscribe();
        }

        channelRef.current = createChatChannel(clientRef.current, roomId);
        channelRef.current.subscribe();
      } catch (err) {
        throw new Error(
          err instanceof Error ? err.message : "Failed to join room",
        );
      }
    },
    [loadMessages],
  );

  const markAsRead = useCallback(async () => {
    setHasNewMessages(false);
  }, []);

  const setTyping = useCallback(async (isTyping: boolean) => {}, []);

  useEffect(() => {
    return () => {
      if (channelRef.current) {
        channelRef.current.unsubscribe();
      }
    };
  }, []);

  return {
    config,
    currentUser: storeCurrentUser,
    currentRoom: storeCurrentRoom,
    messages: storeMessages,
    isLoading,
    error,
    hasNewMessages,
    sendMessage,
    markAsRead,
    setTyping,
    createRoom,
    joinRoom,
    syncNewMessages,
  };
}

async function uploadFile(
  client: ReturnType<typeof createSupabaseClient>,
  file: File,
): Promise<{ url: string; path: string }> {
  const fileExt = file.name.split(".").pop();
  const fileName = `${crypto.randomUUID()}.${fileExt}`;
  const filePath = `${Date.now()}/${fileName}`;

  const { data, error } = await client.storage
    .from("chat-files")
    .upload(filePath, file, {
      cacheControl: "3600",
      upsert: false,
    });

  if (error) {
    throw new Error(`Upload failed: ${error.message}`);
  }

  const {
    data: { publicUrl },
  } = client.storage.from("chat-files").getPublicUrl(filePath);

  return {
    url: publicUrl,
    path: filePath,
  };
}

/**
 * SupaChat - useChatStatus Hook
 * Manages typing indicators and user presence for chat rooms.
 */

"use client";

import {
  createPresenceChannel,
  createSupabaseClient,
} from "@/lib/supabase/client";
import type { SupaChatConfig } from "@/lib/supachat/types";
import { useCallback, useEffect, useRef, useState } from "react";

export function useChatStatus(config: SupaChatConfig) {
  const [isTyping, setIsTyping] = useState(false);
  const [isOnline, setIsOnline] = useState(false);
  const [lastSeen, setLastSeen] = useState<string>(new Date().toISOString());
  const [typingUsers, setTypingUsers] = useState<Set<string>>(new Set());

  const channelRef = useRef<any>(null);
  const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (!config.enablePresence) return;
    const client = createSupabaseClient(
      config.supabaseUrl,
      config.supabaseAnonKey,
    );
    channelRef.current = createPresenceChannel(client, "presence-channel");
    channelRef.current.on("presence", { event: "sync" }, () => {
      const presence = channelRef.current.presenceState();
      const onlineUsers = Object.keys(presence);
      setIsOnline(onlineUsers.length > 0);
    });
    channelRef.current.on("presence", { event: "join" }, () => {
      setLastSeen(new Date().toISOString());
      setIsOnline(true);
    });
    channelRef.current.on("presence", { event: "leave" }, () => {
      setLastSeen(new Date().toISOString());
    });
    channelRef.current.subscribe(async (status: string) => {
      if (status === "SUBSCRIBED") {
        await channelRef.current.track({
          user_id: "guest",
          online_at: new Date().toISOString(),
        });
      }
    });
    return () => {
      if (channelRef.current) {
        channelRef.current.unsubscribe();
      }
    };
  }, [config.supabaseUrl, config.supabaseAnonKey, config.enablePresence]);

  const setTyping = useCallback(
    async (isTyping: boolean) => {
      if (!channelRef.current || !config.enablePresence) return;
      setIsTyping(isTyping);
      if (isTyping) {
        if (typingTimeoutRef.current) {
          clearTimeout(typingTimeoutRef.current);
        }
        await channelRef.current.track({
          user_id: "guest",
          typing: true,
          typing_at: new Date().toISOString(),
        });
        typingTimeoutRef.current = setTimeout(async () => {
          await channelRef.current.track({
            user_id: "guest",
            typing: false,
          });
          setIsTyping(false);
        }, 3000);
      } else {
        if (typingTimeoutRef.current) {
          clearTimeout(typingTimeoutRef.current);
          typingTimeoutRef.current = null;
        }
        await channelRef.current.track({
          user_id: "guest",
          typing: false,
        });
      }
    },
    [config.enablePresence],
  );

  const updateLastSeen = useCallback(async () => {
    if (!channelRef.current || !config.enablePresence) return;
    const now = new Date().toISOString();
    setLastSeen(now);
    await channelRef.current.track({
      user_id: "guest",
      last_seen: now,
    });
  }, [config.enablePresence]);

  useEffect(() => {
    if (!channelRef.current || !config.enablePresence) return;
    channelRef.current.on("presence", { event: "sync" }, () => {
      const presence = channelRef.current.presenceState();
      const typingUsersSet = new Set<string>();
      Object.entries(presence).forEach(([key, presences]: [string, any]) => {
        const userPresence = Array.isArray(presences)
          ? presences[0]
          : presences;
        if (userPresence?.typing && userPresence.user_id !== "guest") {
          typingUsersSet.add(userPresence.user_id);
        }
      });
      setTypingUsers(typingUsersSet);
    });
  }, [config.enablePresence]);

  useEffect(() => {
    if (!config.enablePresence) return;
    const interval = setInterval(() => {
      updateLastSeen();
    }, 30000);
    return () => clearInterval(interval);
  }, [updateLastSeen, config.enablePresence]);

  useEffect(() => {
    return () => {
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
      if (channelRef.current) {
        channelRef.current.unsubscribe();
      }
    };
  }, []);

  return {
    isTyping,
    isOnline,
    lastSeen,
    typingUsers: Array.from(typingUsers),
    setTyping,
    updateLastSeen,
  };
}

/**
 * SupaChat - useAdmin Hook
 * Admin operations, rooms, and messages management for SupaChat.
 */

"use client";

import { createChatStore } from "@/hooks/supachat/use-chat-store";
import {
  createMessage,
  createSupabaseClient,
  getChatRooms,
  getMessages,
} from "@/lib/supabase/client";
import type {
  ChatMessage,
  ChatRoom,
  SupaChatConfig,
} from "@/lib/supachat/types";
import { useCallback, useEffect, useMemo, useRef, useState } from "react";

export function useAdmin(config: SupaChatConfig) {
  const chatStore = useMemo(
    () => createChatStore({ localStorageKey: config.localStorageKey }),
    [config.localStorageKey],
  );
  const [rooms, setRooms] = useState<ChatRoom[]>([]);
  const [selectedRoom, setSelectedRoom] = useState<ChatRoom | null>(null);
  const [roomMessages, setRoomMessages] = useState<
    Record<string, ChatMessage[]>
  >({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const clientRef = useRef<ReturnType<typeof createSupabaseClient> | null>(
    null,
  );

  useEffect(() => {
    clientRef.current = createSupabaseClient(
      config.supabaseUrl,
      config.supabaseAnonKey,
    );
  }, [config.supabaseUrl, config.supabaseAnonKey]);

  const loadRooms = useCallback(async () => {
    if (!clientRef.current) return;

    setIsLoading(true);
    setError(null);

    try {
      const data = await getChatRooms(clientRef.current);
      const transformedRooms: ChatRoom[] = data.map((room: any) => ({
        id: room.id,
        name: room.name || undefined,
        isGuestRoom: room.is_guest_room || false,
        guestSessionId: room.guest_session_id || undefined,
        createdAt: room.created_at || new Date().toISOString(),
        updatedAt: room.updated_at || new Date().toISOString(),
        lastMessage: room.messages?.[0]
          ? {
              id: room.messages[0].id,
              roomId: room.messages[0].room_id || "",
              userId: room.messages[0].user_id || undefined,
              guestSessionId: room.messages[0].guest_session_id || undefined,
              content: room.messages[0].content,
              messageType:
                (room.messages[0].message_type as "text" | "file" | "system") ||
                "text",
              fileUrl: room.messages[0].file_url || undefined,
              fileName: room.messages[0].file_name || undefined,
              fileSize: room.messages[0].file_size || undefined,
              isFromAdmin: room.messages[0].is_from_admin || false,
              createdAt:
                room.messages[0].created_at || new Date().toISOString(),
            }
          : undefined,
        unreadCount: room.unread_count || 0,
      }));
      setRooms(transformedRooms);
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to load rooms");
    } finally {
      setIsLoading(false);
    }
  }, []);

  const loadRoomMessages = useCallback(async (roomId: string) => {
    if (!clientRef.current || !roomId) return;

    try {
      const data = await getMessages(clientRef.current, roomId);
      const transformedMessages: ChatMessage[] = data.map((msg: any) => ({
        id: msg.id,
        roomId: msg.room_id || "",
        userId: msg.user_id || undefined,
        guestSessionId: msg.guest_session_id || undefined,
        content: msg.content,
        messageType: (msg.message_type as "text" | "file" | "system") || "text",
        fileUrl: msg.file_url || undefined,
        fileName: msg.file_name || undefined,
        fileSize: msg.file_size || undefined,
        isFromAdmin: msg.is_from_admin || false,
        createdAt: msg.created_at || new Date().toISOString(),
      }));
      setRoomMessages((prev) => ({
        ...prev,
        [roomId]: transformedMessages,
      }));
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to load messages");
    }
  }, []);

  const sendAdminMessage = useCallback(
    async (roomId: string, content: string) => {
      if (!clientRef.current || !roomId || !content.trim()) return;

      try {
        const messageData = await createMessage(
          clientRef.current,
          roomId,
          content,
          {
            isFromAdmin: true,
            messageType: "text",
          },
        );

        const newMessage: ChatMessage = {
          id: messageData.id,
          roomId: messageData.room_id || "",
          userId: messageData.user_id || undefined,
          guestSessionId: messageData.guest_session_id || undefined,
          content: messageData.content,
          messageType:
            (messageData.message_type as "text" | "file" | "system") || "text",
          fileUrl: messageData.file_url || undefined,
          fileName: messageData.file_name || undefined,
          fileSize: messageData.file_size || undefined,
          isFromAdmin: messageData.is_from_admin || false,
          createdAt: messageData.created_at || new Date().toISOString(),
        };

        setRoomMessages((prev) => ({
          ...prev,
          [roomId]: [...(prev[roomId] || []), newMessage],
        }));

        setRooms((prev) =>
          prev.map((room) =>
            room.id === roomId
              ? {
                  ...room,
                  lastMessage: newMessage,
                  updatedAt: new Date().toISOString(),
                }
              : room,
          ),
        );
      } catch (err) {
        setError(err instanceof Error ? err.message : "Failed to send message");
      }
    },
    [],
  );

  const deleteRoom = useCallback(
    async (roomId: string) => {
      if (!clientRef.current || !roomId) return;

      try {
        const { error } = await clientRef.current
          .from("chat_rooms")
          .delete()
          .eq("id", roomId);

        if (error) throw error;

        setRooms((prev) => prev.filter((room) => room.id !== roomId));
        setRoomMessages((prev) => {
          const newMessages = { ...prev };
          delete newMessages[roomId];
          return newMessages;
        });

        if (selectedRoom?.id === roomId) {
          setSelectedRoom(null);
        }
      } catch (err) {
        setError(err instanceof Error ? err.message : "Failed to delete room");
      }
    },
    [selectedRoom?.id],
  );

  const markAsRead = useCallback(async (roomId: string) => {
    if (!clientRef.current || !roomId) return;

    try {
      const { error } = await clientRef.current.from("chat_users").upsert({
        user_id: "admin",
        last_seen: new Date().toISOString(),
      });

      if (error) throw error;

      setRooms((prev) =>
        prev.map((room) =>
          room.id === roomId ? { ...room, unreadCount: 0 } : room,
        ),
      );
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to mark as read");
    }
  }, []);

  const assignAdmin = useCallback(async (roomId: string, adminId: string) => {
    if (!clientRef.current || !roomId || !adminId) return;

    try {
      const { error } = await clientRef.current
        .from("chat_rooms")
        .update({ assigned_admin_id: adminId })
        .eq("id", roomId);

      if (error) throw error;

      setRooms((prev) =>
        prev.map((room) =>
          room.id === roomId ? { ...room, assignedAdminId: adminId } : room,
        ),
      );
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to assign admin");
    }
  }, []);

  const getRoomStats = useCallback(
    (roomId: string) => {
      const messages = roomMessages[roomId] || [];
      const totalMessages = messages.length;
      const adminMessages = messages.filter((m) => m.isFromAdmin).length;
      const userMessages = totalMessages - adminMessages;
      const lastActivity =
        messages.length > 0 ? messages[messages.length - 1].createdAt : null;

      return {
        totalMessages,
        adminMessages,
        userMessages,
        lastActivity,
      };
    },
    [roomMessages],
  );

  useEffect(() => {
    loadRooms();
  }, [loadRooms]);

  useEffect(() => {
    if (!clientRef.current) return;

    const roomsChannel = clientRef.current
      .channel("admin-rooms")
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "chat_rooms",
        },
        (payload) => {
          if (payload.eventType === "INSERT") {
            const newRoom: ChatRoom = {
              id: payload.new.id,
              name: payload.new.name || undefined,
              isGuestRoom: payload.new.is_guest_room || false,
              guestSessionId: payload.new.guest_session_id || undefined,
              createdAt: payload.new.created_at || new Date().toISOString(),
              updatedAt: payload.new.updated_at || new Date().toISOString(),
              unreadCount: 0,
            };
            setRooms((prev) => [newRoom, ...prev]);
          } else if (payload.eventType === "UPDATE") {
            const updatedRoom: ChatRoom = {
              id: payload.new.id,
              name: payload.new.name || undefined,
              isGuestRoom: payload.new.is_guest_room || false,
              guestSessionId: payload.new.guest_session_id || undefined,
              createdAt: payload.new.created_at || new Date().toISOString(),
              updatedAt: payload.new.updated_at || new Date().toISOString(),
              unreadCount: 0,
            };
            setRooms((prev) =>
              prev.map((room) =>
                room.id === updatedRoom.id ? updatedRoom : room,
              ),
            );
          } else if (payload.eventType === "DELETE") {
            setRooms((prev) =>
              prev.filter((room) => room.id !== payload.old.id),
            );
          }
        },
      )
      .subscribe();

    const messagesChannel = clientRef.current
      .channel("admin-messages")
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "messages",
        },
        (payload) => {
          if (payload.eventType === "INSERT") {
            const newMessage: ChatMessage = {
              id: payload.new.id,
              roomId: payload.new.room_id || "",
              userId: payload.new.user_id || undefined,
              guestSessionId: payload.new.guest_session_id || undefined,
              content: payload.new.content,
              messageType:
                (payload.new.message_type as "text" | "file" | "system") ||
                "text",
              fileUrl: payload.new.file_url || undefined,
              fileName: payload.new.file_name || undefined,
              fileSize: payload.new.file_size || undefined,
              isFromAdmin: payload.new.is_from_admin || false,
              createdAt: payload.new.created_at || new Date().toISOString(),
            };
            setRoomMessages((prev) => ({
              ...prev,
              [newMessage.roomId]: [
                ...(prev[newMessage.roomId] || []),
                newMessage,
              ],
            }));
          }
        },
      )
      .subscribe();

    return () => {
      roomsChannel.unsubscribe();
      messagesChannel.unsubscribe();
    };
  }, []);

  return {
    rooms,
    selectedRoom,
    roomMessages,
    isLoading,
    error,
    sendAdminMessage,
    deleteRoom,
    markAsRead,
    assignAdmin,
    getRoomStats,
    loadRooms,
    loadRoomMessages,
    setSelectedRoom,
  };
}

